// src/pages/EditQuote.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const EditQuote = () => {
  const { id } = useParams();
  const [quote, setQuote] = useState({
    title: '',
    amount: ''
  });

  useEffect(() => {
    const fetchQuote = async () => {
      const response = await axios.get(`http://localhost:4000/api/quotes/${id}`);
      setQuote(response.data);
    };
    fetchQuote();
  }, [id]);

  const handleChange = (e) => {
    setQuote({
      ...quote,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/quotes/${id}`, quote);
      window.location.href = '/quotes/view';
    } catch (error) {
      console.error('Error updating quote:', error);
    }
  };

  return (
    <div>
      <h1>Edit Quote</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={quote.title} onChange={handleChange} required />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={quote.amount} onChange={handleChange} required />
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditQuote;
