import React, { useState } from 'react';
import axios from 'axios';

const TimeSheetForm = () => {
    const [employee, setEmployee] = useState('');
    const [hoursWorked, setHoursWorked] = useState('');
    const [jobId, setJobId] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validation
        if (!employee || !hoursWorked || !jobId) {
            setError('All fields are required');
            setSuccess('');
            return;
        }

        setError('');

        try {
            const response = await axios.post('/api/timesheets', {
                employee,
                hoursWorked,
                jobId,
            });
            console.log('Response from server:', response.data); // Debug log
            setSuccess('Time sheet added successfully!');
            setEmployee('');
            setHoursWorked('');
            setJobId('');
        } catch (error) {
            console.error('Error adding time sheet:', error); // Log error details
            setError('Error adding time sheet. Please try again.');
            setSuccess('');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Employee:</label>
            <input
                type="text"
                value={employee}
                onChange={(e) => setEmployee(e.target.value)}
            />
            <label>Hours Worked:</label>
            <input
                type="number"
                value={hoursWorked}
                onChange={(e) => setHoursWorked(e.target.value)}
            />
            <label>Job ID:</label>
            <input
                type="text"
                value={jobId}
                onChange={(e) => setJobId(e.target.value)}
            />
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {success && <p style={{ color: 'green' }}>{success}</p>}
            <button type="submit">Add Time Sheet</button>
        </form>
    );
};

export default TimeSheetForm;
