// src/pages/EditJob.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const EditJob = () => {
  const { id } = useParams();
  const [job, setJob] = useState({
    title: '',
    description: '',
    client: '',
    status: '',
    dueDate: '',
    assignedTo: ''
  });

  useEffect(() => {
    const fetchJob = async () => {
      const response = await axios.get(`http://localhost:4000/api/jobs/${id}`);
      setJob(response.data);
    };
    fetchJob();
  }, [id]);

  const handleChange = (e) => {
    setJob({
      ...job,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/jobs/${id}`, job);
      window.location.href = '/jobs/view';
    } catch (error) {
      console.error('Error updating job:', error);
    }
  };

  return (
    <div>
      <h1>Edit Job</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" name="title" value={job.title} onChange={handleChange} required />
        </label>
        <label>
          Description:
          <input type="text" name="description" value={job.description} onChange={handleChange} required />
        </label>
        <label>
          Client:
          <input type="text" name="client" value={job.client} onChange={handleChange} required />
        </label>
        <label>
          Status:
          <input type="text" name="status" value={job.status} onChange={handleChange} required />
        </label>
        <label>
          Due Date:
          <input type="date" name="dueDate" value={job.dueDate} onChange={handleChange} required />
        </label>
        <label>
          Assigned To:
          <input type="text" name="assignedTo" value={job.assignedTo} onChange={handleChange} required />
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditJob;
