// src/pages/ViewJobs.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewJobs = () => {
  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    const fetchJobs = async () => {
      const response = await axios.get('http://localhost:4000/api/jobs');
      setJobs(response.data);
    };
    fetchJobs();
  }, []);

  const deleteJob = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/jobs/${id}`);
      setJobs(jobs.filter(job => job._id !== id));
    } catch (error) {
      console.error('Error deleting job:', error);
    }
  };

  const editJob = (id) => {
    window.location.href = `/jobs/edit/${id}`;
  };

  return (
    <div>
      <h1>Jobs</h1>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Client</th>
            <th>Status</th>
            <th>Due Date</th>
            <th>Assigned To</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {jobs.map(job => (
            <tr key={job._id}>
              <td>{job.title}</td>
              <td>{job.description}</td>
              <td>{job.client}</td>
              <td>{job.status}</td>
              <td>{new Date(job.dueDate).toLocaleDateString()}</td>
              <td>{job.assignedTo}</td>
              <td>
                <button onClick={() => editJob(job._id)}>Edit</button>
                <button onClick={() => deleteJob(job._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ViewJobs;
