// src/pages/ViewQuotes.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewQuotes = () => {
  const [quotes, setQuotes] = useState([]);

  useEffect(() => {
    const fetchQuotes = async () => {
      const response = await axios.get('http://localhost:4000/api/quotes');
      setQuotes(response.data);
    };
    fetchQuotes();
  }, []);

  const deleteQuote = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/quotes/${id}`);
      setQuotes(quotes.filter(quote => quote._id !== id));
    } catch (error) {
      console.error('Error deleting quote:', error);
    }
  };

  const editQuote = (id) => {
    window.location.href = `/quotes/edit/${id}`;
  };

  return (
    <div>
      <h1>Quotes</h1>
      <ul>
        {quotes.map(quote => (
          <li key={quote._id}>
            {quote.title} - {quote.amount}
            <button onClick={() => editQuote(quote._id)}>Edit</button>
            <button onClick={() => deleteQuote(quote._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ViewQuotes;
