// src/pages/Clients.js
import React, { useState } from 'react';
import axios from 'axios';

const Clients = () => {
  const [formData, setFormData] = useState({
    name: '',
    contactInfo: {
      email: '',
      phone: '',
    },
    address: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('contactInfo')) {
      const contactField = name.split('.')[1];
      setFormData({
        ...formData,
        contactInfo: {
          ...formData.contactInfo,
          [contactField]: value,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/clients', formData);
      console.log(response.data);
      alert('Client created successfully!');
    } catch (error) {
      console.error('There was an error creating the client!', error);
    }
  };

  return (
    <div>
      <h1>Create Client</h1>
      <form onSubmit={handleSubmit}>
        <label>Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>Email:
          <input type="email" name="contactInfo.email" value={formData.contactInfo.email} onChange={handleChange} required />
        </label>
        <label>Phone:
          <input type="tel" name="contactInfo.phone" value={formData.contactInfo.phone} onChange={handleChange} required />
        </label>
        <label>Address:
          <input type="text" name="address" value={formData.address} onChange={handleChange} />
        </label>
        <button type="submit">Create Client</button>
      </form>
    </div>
  );
};

export default Clients;
