// src/pages/Invoices.js
import React, { useState } from 'react';
import axios from 'axios';

const Invoices = () => {
  const [formData, setFormData] = useState({
    quoteId: '',
    amountDue: '',
    dueDate: '',
    status: 'Pending',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/invoices', formData);
      console.log(response.data);
      alert('Invoice created successfully!');
    } catch (error) {
      console.error('There was an error creating the invoice!', error);
    }
  };

  return (
    <div>
      <h1>Create Invoice</h1>
      <form onSubmit={handleSubmit}>
        <label>Quote ID:
          <input type="text" name="quoteId" value={formData.quoteId} onChange={handleChange} required />
        </label>
        <label>Amount Due:
          <input type="number" name="amountDue" value={formData.amountDue} onChange={handleChange} required />
        </label>
        <label>Due Date:
          <input type="date" name="dueDate" value={formData.dueDate} onChange={handleChange} required />
        </label>
        <label>Status:
          <select name="status" value={formData.status} onChange={handleChange}>
            <option value="Pending">Pending</option>
            <option value="Paid">Paid</option>
            <option value="Overdue">Overdue</option>
          </select>
        </label>
        <button type="submit">Create Invoice</button>
      </form>
    </div>
  );
};

export default Invoices;
