import React, { useState } from 'react';
import axios from 'axios';

const JobForm = () => {
    const [jobTitle, setJobTitle] = useState('');
    const [client, setClient] = useState('');
    const [estimatedDuration, setEstimatedDuration] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [loading, setLoading] = useState(false); // Loading state

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("Form submitted", { jobTitle, client, estimatedDuration });

        // Validation
        if (!jobTitle || !client || !estimatedDuration || isNaN(estimatedDuration)) {
            setError('All fields are required and duration must be a number');
            setSuccess('');
            return;
        }

        setError('');
        setLoading(true); // Start loading

        try {
            await axios.post('/api/jobs', {
                jobTitle,
                client,
                estimatedDuration,
            });
            setSuccess('Job added successfully!');
            setJobTitle('');
            setClient('');
            setEstimatedDuration('');
        } catch (error) {
            setError('Error adding job. Please try again.');
            setSuccess('');
        } finally {
            setLoading(false); // End loading
        }
    };

    return (
        <div className="form-container">
            <form onSubmit={handleSubmit} className="job-form">
                <label htmlFor="jobTitle">Job Title:</label>
                <input
                    type="text"
                    id="jobTitle"
                    value={jobTitle}
                    onChange={(e) => setJobTitle(e.target.value)}
                    className={error && !jobTitle ? 'error' : ''}
                />
                <label htmlFor="client">Client:</label>
                <input
                    type="text"
                    id="client"
                    value={client}
                    onChange={(e) => setClient(e.target.value)}
                    className={error && !client ? 'error' : ''}
                />
                <label htmlFor="estimatedDuration">Estimated Duration:</label>
                <input
                    type="text"
                    id="estimatedDuration"
                    value={estimatedDuration}
                    onChange={(e) => setEstimatedDuration(e.target.value)}
                    className={error && (isNaN(estimatedDuration) || !estimatedDuration) ? 'error' : ''}
                />
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {success && <p style={{ color: 'green' }}>{success}</p>}
                <button type="submit" className="button" disabled={loading}>
                    {loading ? 'Adding...' : 'Add Job'}
                </button>
            </form>
        </div>
    );
};

export default JobForm;
