// src/pages/Quotes.js
import React, { useState } from 'react';
import axios from 'axios';

const Quotes = () => {
  const [formData, setFormData] = useState({
    jobId: '',
    amount: '',
    date: '',
    status: 'Pending',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/api/quotes', formData);
      console.log(response.data);
      alert('Quote created successfully!');

      // Clear the form after successful submission
      setFormData({
        jobId: '',
        amount: '',
        date: '',
        status: 'Pending',
      });
    } catch (error) {
      console.error('There was an error creating the quote!', error);
      alert('Error: ' + (error.response?.data?.message || 'An error occurred.'));
    }
  };

  return (
    <div>
      <h1>Create Quote</h1>
      <form onSubmit={handleSubmit}>
        <label>Job ID:
          <input type="text" name="jobId" value={formData.jobId} onChange={handleChange} required />
        </label>
        <label>Amount:
          <input type="number" name="amount" value={formData.amount} onChange={handleChange} required />
        </label>
        <label>Date:
          <input type="date" name="date" value={formData.date} onChange={handleChange} required />
        </label>
        <label>Status:
          <select name="status" value={formData.status} onChange={handleChange}>
            <option value="Pending">Pending</option>
            <option value="Accepted">Accepted</option>
            <option value="Declined">Declined</option>
          </select>
        </label>
        <button type="submit">Create Quote</button>
      </form>
    </div>
  );
};

export default Quotes;
