// src/pages/Jobs.js
import React, { useState } from 'react';
import axios from 'axios';

const Jobs = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    client: '',
    status: 'Pending',
    dueDate: '',
    assignedTo: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting job:', formData); // Debugging log
    try {
      const response = await axios.post('http://localhost:4000/api/jobs', formData);
      console.log(response.data);
      alert('Job created successfully!');
    } catch (error) {
      console.error('There was an error creating the job!', error.response ? error.response.data : error.message);
    }
  };

  return (
    <div>
      <h1>Create Job</h1>
      <form onSubmit={handleSubmit}>
        <label>Title:
          <input type="text" name="title" value={formData.title} onChange={handleChange} required />
        </label>
        <label>Description:
          <input type="text" name="description" value={formData.description} onChange={handleChange} required />
        </label>
        <label>Client:
          <input type="text" name="client" value={formData.client} onChange={handleChange} required />
        </label>
        <label>Status:
          <select name="status" value={formData.status} onChange={handleChange}>
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </label>
        <label>Due Date:
          <input type="date" name="dueDate" value={formData.dueDate} onChange={handleChange} required />
        </label>
        <label>Assigned To:
          <input type="text" name="assignedTo" value={formData.assignedTo} onChange={handleChange} required />
        </label>
        <button type="submit">Create Job</button>
      </form>
    </div>
  );
};

export default Jobs;
