// src/pages/Clients.js
import React, { useState } from 'react';
import axios from 'axios';

const Clients = () => {
  const [formData, setFormData] = useState({
    name: '',
    contactInfo: {
      email: '',
      phone: '',
    },
    address: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name.startsWith('contactInfo.')) {
      const contactField = name.split('.')[1];
      setFormData((prevData) => ({
        ...prevData,
        contactInfo: {
          ...prevData.contactInfo,
          [contactField]: value,
        },
      }));
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission

    // Validate that email is not null or empty
    if (!formData.contactInfo.email) {
      alert('Email is required!');
      return; // Exit the function if email is empty
    }

    try {
      const response = await axios.post('http://localhost:4000/api/clients', formData);
      console.log("Response:", response.data); // Log the response from the server
      alert('Client created successfully!');

      // Clear the form after successful submission
      setFormData({
        name: '',
        contactInfo: {
          email: '',
          phone: '',
        },
        address: '',
      });
    } catch (error) {
      console.error('There was an error creating the client!', error);
      alert('Error: ' + (error.response.data.message || 'An error occurred.')); // Show error message
    }
  };

  return (
    <div>
      <h1>Create Client</h1>
      <form onSubmit={handleSubmit}>
        <label>Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>Email:
          <input type="email" name="contactInfo.email" value={formData.contactInfo.email} onChange={handleChange} required />
        </label>
        <label>Phone:
          <input type="text" name="contactInfo.phone" value={formData.contactInfo.phone} onChange={handleChange} required />
        </label>
        <label>Address:
          <input type="text" name="address" value={formData.address} onChange={handleChange} required />
        </label>
        <button type="submit">Create Client</button>
      </form>
    </div>
  );
};

export default Clients;
