import React, { useState } from 'react';
import axios from 'axios';

const Clients = () => {
  const [formData, setFormData] = useState({
    name: '',
    contactInfo: {
      email: '',
      phone: '',
    },
    address: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Check if the field belongs to contactInfo
    if (name.includes('contactInfo')) {
      const contactField = name.split('.')[1]; // Get the specific field (email or phone)
      setFormData((prevData) => ({
        ...prevData,
        contactInfo: {
          ...prevData.contactInfo,
          [contactField]: value,
        },
      }));
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate all required fields
    if (!formData.name || !formData.contactInfo.email || !formData.contactInfo.phone || !formData.address) {
      alert("Please fill in all required fields.");
      return;
    }

    try {
      await axios.post('http://localhost:4000/api/clients', formData);
      alert('Client created successfully!');
      
      // Clear the form after successful submission
      setFormData({
        name: '',
        contactInfo: {
          email: '',
          phone: '',
        },
        address: '',
      });
    } catch (error) {
      console.error('Error:', error.response?.data?.message || error.message);
      alert('Error: ' + (error.response?.data?.message || 'An error occurred.'));
    }
  };

  return (
    <div>
      <h1>Create Client</h1>
      <form onSubmit={handleSubmit}>
        <label>Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>Contact Email:
          <input type="email" name="contactInfo.email" value={formData.contactInfo.email} onChange={handleChange} required />
        </label>
        <label>Contact Phone:
          <input type="tel" name="contactInfo.phone" value={formData.contactInfo.phone} onChange={handleChange} required />
        </label>
        <label>Address:
          <input type="text" name="address" value={formData.address} onChange={handleChange} required />
        </label>
        <button type="submit">Create Client</button>
      </form>
    </div>
  );
};

export default Clients;
