// src/pages/EditProject.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const EditProject = () => {
  const { id } = useParams();
  const [project, setProject] = useState({
    name: '',
    description: ''
  });

  useEffect(() => {
    const fetchProject = async () => {
      const response = await axios.get(`http://localhost:4000/api/projects/${id}`);
      setProject(response.data);
    };
    fetchProject();
  }, [id]);

  const handleChange = (e) => {
    setProject({
      ...project,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/projects/${id}`, project);
      window.location.href = '/projects/view';
    } catch (error) {
      console.error('Error updating project:', error);
    }
  };

  return (
    <div>
      <h1>Edit Project</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Project Name:
          <input type="text" name="name" value={project.name} onChange={handleChange} required />
        </label>
        <label>
          Description:
          <input type="text" name="description" value={project.description} onChange={handleChange} required />
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditProject;
