// src/pages/Quotes.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Quotes = () => {
  const [formData, setFormData] = useState({
    jobId: '',
    totalAmount: '',
    expiresOn: '',
    client: '',
    description: '',
    title: '',
    status: 'Pending',
  });

  const [jobs, setJobs] = useState([]);

  useEffect(() => {
    // Fetch jobs to populate jobId dropdown
    const fetchJobs = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/jobs');
        setJobs(response.data);
      } catch (error) {
        console.error('Error fetching jobs:', error);
      }
    };

    fetchJobs();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/api/quotes', formData);
      console.log(response.data);
      alert('Quote created successfully!');

      // Clear the form after successful submission
      setFormData({
        jobId: '',
        totalAmount: '',
        expiresOn: '',
        client: '',
        description: '',
        title: '',
        status: 'Pending',
      });
    } catch (error) {
      console.error('There was an error creating the quote!', error);
      alert('Error: ' + (error.response?.data?.message || 'An error occurred.'));
    }
  };

  return (
    <div>
      <h1>Create Quote</h1>
      <form onSubmit={handleSubmit}>
        <label>Job ID:
          <select name="jobId" value={formData.jobId} onChange={handleChange} required>
            <option value="">Select a job</option>
            {jobs.map((job) => (
              <option key={job._id} value={job._id}>
                {job.title} (ID: {job._id}) {/* Change according to your job model */}
              </option>
            ))}
          </select>
        </label>
        <label>Total Amount:
          <input type="number" name="totalAmount" value={formData.totalAmount} onChange={handleChange} required />
        </label>
        <label>Expires On:
          <input type="date" name="expiresOn" value={formData.expiresOn} onChange={handleChange} required />
        </label>
        <label>Client ID:
          <input type="text" name="client" value={formData.client} onChange={handleChange} required />
        </label>
        <label>Description:
          <textarea name="description" value={formData.description} onChange={handleChange} required />
        </label>
        <label>Title:
          <input type="text" name="title" value={formData.title} onChange={handleChange} required />
        </label>
        <label>Status:
          <select name="status" value={formData.status} onChange={handleChange}>
            <option value="Pending">Pending</option>
            <option value="Accepted">Accepted</option>
            <option value="Declined">Declined</option>
          </select>
        </label>
        <button type="submit">Create Quote</button>
      </form>
    </div>
  );
};

export default Quotes;
