import React, { useState } from 'react';
import './styles/App.css';
import Logo from './components/logo.jfif';

function App() {
    const [activeSection, setActiveSection] = useState('home');

    const menuOptions = [
        { name: 'Home', key: 'home' },
        { name: 'Jobs', key: 'jobs' },
        { name: 'Clients', key: 'clients' },
        // Add other sections as needed
    ];

    return (
        <div className="app-container">
            <div className="sidebar">
                <img src={Logo} alt="Logo" className="logo" />
                <nav className="menu">
                    {menuOptions.map(option => (
                        <button
                            key={option.key}
                            className={`menu-item ${activeSection === option.key ? 'active' : ''}`}
                            onClick={() => setActiveSection(option.key)}
                        >
                            {option.name}
                        </button>
                    ))}
                </nav>
            </div>
            <div className="content">
                {activeSection === 'jobs' && <div>Jobs Section</div>}
                {activeSection === 'clients' && <div>Clients Section</div>}
                {/* Add more sections based on `activeSection` */}
            </div>
        </div>
    );
}

export default App;
