import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TimeSheetList = () => {
    const [timeSheets, setTimeSheets] = useState([]);
    const [error, setError] = useState('');

    const fetchTimeSheets = async () => {
        try {
            const response = await axios.get('/api/timesheets');
            setTimeSheets(response.data);
            setError(''); // Clear error if the fetch is successful
        } catch (error) {
            console.error('Error fetching time sheets:', error);
            setError('Error fetching time sheets. Please try again later.');
        }
    };

    useEffect(() => {
        fetchTimeSheets();
    }, []);

    return (
        <div>
            <h2>Time Sheet List</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <ul>
                {timeSheets.length > 0 ? (
                    timeSheets.map((timeSheet) => (
                        <li key={timeSheet._id}>
                            Job ID: {timeSheet.jobId} - Employee ID: {timeSheet.employeeId} - Date: {timeSheet.date} - Hours: {timeSheet.hoursWorked} - Notes: {timeSheet.notes}
                        </li>
                    ))
                ) : (
                    <p>No time sheets available.</p>
                )}
            </ul>
        </div>
    );
};

export default TimeSheetList;
