// src/pages/EditInvoice.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const EditInvoice = () => {
  const { id } = useParams();
  const [invoice, setInvoice] = useState({
    number: '',
    amount: ''
  });

  useEffect(() => {
    const fetchInvoice = async () => {
      const response = await axios.get(`http://localhost:4000/api/invoices/${id}`);
      setInvoice(response.data);
    };
    fetchInvoice();
  }, [id]);

  const handleChange = (e) => {
    setInvoice({
      ...invoice,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:4000/api/invoices/${id}`, invoice);
      window.location.href = '/invoices/view';
    } catch (error) {
      console.error('Error updating invoice:', error);
    }
  };

  return (
    <div>
      <h1>Edit Invoice</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Invoice Number:
          <input type="text" name="number" value={invoice.number} onChange={handleChange} required />
        </label>
        <label>
          Amount:
          <input type="number" name="amount" value={invoice.amount} onChange={handleChange} required />
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditInvoice;
