import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Inventories = () => {
  const [formData, setFormData] = useState({
    itemName: '',
    quantity: '',
    location: '',
    price: '',
    description: '',
  });

  const [inventories, setInventories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/api/inventories', formData);
      alert('Inventory item created successfully!');
      fetchInventories(); // Refresh the inventory list
    } catch (error) {
      console.error('There was an error creating the inventory item!', error);
      setError('Error creating inventory item. Please try again.');
    }
  };

  const fetchInventories = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:4000/api/inventories');
      setInventories(response.data);
    } catch (error) {
      console.error('Error fetching inventories:', error);
      setError('Error fetching inventories. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchInventories(); // Fetch inventories when the component mounts
  }, []);

  return (
    <div>
      <h1>Create Inventory Item</h1>
      <form onSubmit={handleSubmit}>
        <label>Item Name:
          <input type="text" name="itemName" value={formData.itemName} onChange={handleChange} required />
        </label>
        <label>Quantity:
          <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} required />
        </label>
        <label>Location:
          <input type="text" name="location" value={formData.location} onChange={handleChange} required />
        </label>
        <label>Price:
          <input type="number" name="price" value={formData.price} onChange={handleChange} required />
        </label>
        <label>Description:
          <input type="text" name="description" value={formData.description} onChange={handleChange} required />
        </label>
        <button type="submit">Create Inventory Item</button>
      </form>
    </div>
  );
};

export default Inventories;
