import React, { useState } from 'react';
import axios from 'axios';

const QuoteForm = () => {
    const [client, setClient] = useState('');
    const [amount, setAmount] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting quote:', { client, amount, description }); // Debug log

        // Validation
        if (!client || !amount || !description) {
            setError('All fields are required');
            setSuccess('');
            console.log('Validation error:', error); // Debug log
            return;
        }

        setError('');

        try {
            await axios.post('/api/quotes', {
                client,
                amount,
                description,
            });
            console.log('Quote added successfully!'); // Debug log
            setSuccess('Quote added successfully!');
            setClient('');
            setAmount('');
            setDescription('');
        } catch (error) {
            console.error('Error adding quote:', error); // Debug log
            setError('Error adding quote. Please try again.');
            setSuccess('');
        }
    };

    return (
        <div>
            <h2>Add a New Quote</h2>
            <form onSubmit={handleSubmit}>
                <label>Client:</label>
                <input
                    type="text"
                    value={client}
                    onChange={(e) => setClient(e.target.value)}
                />
                <label>Amount:</label>
                <input
                    type="number"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                />
                <label>Description:</label>
                <textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {success && <p style={{ color: 'green' }}>{success}</p>}
                <button type="submit">Add Quote</button>
            </form>
        </div>
    );
};

export default QuoteForm;
