// src/pages/Inventories.js
import React, { useState } from 'react';
import axios from 'axios';

const Inventories = () => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    quantity: 0,
    price: 0,
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:4000/api/inventories', formData);
      console.log(response.data);
      alert('Inventory item created successfully!');
    } catch (error) {
      console.error('There was an error creating the inventory item!', error);
    }
  };

  return (
    <div>
      <h1>Create Inventory Item</h1>
      <form onSubmit={handleSubmit}>
        <label>Name:
          <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        </label>
        <label>Description:
          <input type="text" name="description" value={formData.description} onChange={handleChange} required />
        </label>
        <label>Quantity:
          <input type="number" name="quantity" value={formData.quantity} onChange={handleChange} required />
        </label>
        <label>Price:
          <input type="number" name="price" value={formData.price} onChange={handleChange} required />
        </label>
        <button type="submit">Create Inventory Item</button>
      </form>
    </div>
  );
};

export default Inventories;
