import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewInvoices = () => {
  const [invoices, setInvoices] = useState([]);

  useEffect(() => {
    const fetchInvoices = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/invoices');
        console.log('Fetched Invoices from Server:', response.data);  // Debug print to check data
        if (response.data) {
          setInvoices(response.data);
          console.log('Set Invoices State:', response.data);  // Debug print to check state setting
        }
      } catch (error) {
        console.error('Error fetching invoices:', error);
      }
    };
    fetchInvoices();
  }, []);

  const deleteInvoice = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/invoices/${id}`);
      setInvoices(invoices.filter(invoice => invoice._id !== id));
    } catch (error) {
      console.error('Error deleting invoice:', error);
    }
  };

  const editInvoice = (id) => {
    window.location.href = `/invoices/edit/${id}`;
  };

  return (
    <div>
      <h1>Existing Invoices</h1>
      <ul>
        {invoices.map(invoice => (
          <li key={invoice._id}>
            {invoice.number} - {invoice.amount}
            <button onClick={() => editInvoice(invoice._id)}>Edit</button>
            <button onClick={() => deleteInvoice(invoice._id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ViewInvoices;
