// src/components/JobComponent.js
import React, { useEffect, useState } from 'react';
import { getJobs, createJob } from '../api/api';

const JobComponent = () => {
    const [jobs, setJobs] = useState([]);
    const [newJob, setNewJob] = useState({
        title: '',
        description: '',
        client: '',
        status: 'Pending',
        dueDate: '',
        assignedTo: ''
    });

    useEffect(() => {
        const fetchJobs = async () => {
            try {
                const response = await getJobs();
                setJobs(response.data);
            } catch (error) {
                console.error('Error fetching jobs:', error);
            }
        };

        fetchJobs();
    }, []);

    const handleChange = (e) => {
        setNewJob({
            ...newJob,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createJob(newJob);
            setNewJob({ title: '', description: '', client: '', status: 'Pending', dueDate: '', assignedTo: '' });
            // Optionally refetch jobs
            const response = await getJobs();
            setJobs(response.data);
        } catch (error) {
            console.error('Error creating job:', error);
        }
    };

    return (
        <div>
            <h2>Jobs</h2>
            <form onSubmit={handleSubmit}>
                <input name="title" value={newJob.title} onChange={handleChange} placeholder="Job Title" required />
                <input name="description" value={newJob.description} onChange={handleChange} placeholder="Description" required />
                <input name="client" value={newJob.client} onChange={handleChange} placeholder="Client" required />
                <input name="dueDate" value={newJob.dueDate} onChange={handleChange} type="date" required />
                <input name="assignedTo" value={newJob.assignedTo} onChange={handleChange} placeholder="Assigned To" required />
                <button type="submit">Create Job</button>
            </form>

            <ul>
                {jobs.map((job) => (
                    <li key={job._id}>{job.title} - {job.status}</li>
                ))}
            </ul>
        </div>
    );
};

export default JobComponent;
